import React from "react";

function BreakInterval(props) {
  function decreaseSession() {
    if (props.breakLength === 1) {
      return;
    }
    props.decreaseBreak();
  }
  function increaseSession() {
    if (props.breakLength === 60) {
      return;
    }
    props.increaseBreak();
  }
  return (
    <section>
      <h4 className="length-title">Break Length:</h4>
      <section className="interval-container">
        <button onClick={decreaseSession}>Down</button>
        <p className="length">{props.breakLength}</p>
        <button onClick={increaseSession}>Up</button>
      </section>
    </section>
  );
}

export default BreakInterval;
